class Solution:    def smallestRangeI(self, A, K):        """        :type A: List[int]        :type K: int        :rtype: int        """        index_small=float("inf")        index_max=float("-inf")        for i in A:            index_small=min(index_small,i)            index_max=max(index_max,i)        ans=max(0,index_max-index_small-2*K)        return anss=Solution()A=[0,10]K=2print(s.smallestRangeI(A,K))