class Solution:    def integerBreak(self, n):        """        :type n: int        :rtype: int        """        if n==2:            return 1        elif n==3:            return 2        if n%3==1:            index3=int((n-4)/3)            return 3**index3*4        elif n%3==2:            index3 = int((n - 2) / 3)            return 3 ** index3 * 2        else:            index3 = int(n / 3)            return 3 ** index3s=Solution()n=10print(s.integerBreak(n))