class Solution:    def checkSubarraySum(self, nums, k):        k = abs(k)        pre = None        s = 0        psum = set()        for n in nums:            s = (s + n) % k if k else s + n            if s in psum:                return True            if pre is not None:                psum.add(pre)            pre = s        return (s == 0 and len(nums) > 1)"""class Solution:    def checkSubarraySum(self, nums, k):        n=len(nums)        for i in range(n):            sum=nums[i]            for j in range(i+1,n):                sum+=nums[j]                if j-i+1>=2:                    if (k==0 and sum==0)or(k!=0 and sum%k==0):                        return True        return False"""nums=[0,1,0]k=0s=Solution()print(s.checkSubarraySum(nums,k))