class Solution(object):    def findLongestChain(self, pairs):        """        :type pairs: List[List[int]]        :rtype: int        """        pairs.sort(key=lambda x:x[1])        cur,res=-float("inf"),0        for item in pairs:            if item[0]>cur:                cur=item[1]                res+=1        return ress=Solution()input=[[1,2], [2,3], [3,4]]print(s.findLongestChain(input))#按照pairs[1] 来排序