# Definition for a binary tree node.class TreeNode:     def __init__(self, x):         self.val = x         self.left = None         self.right = Noneclass Solution:    def buildTree(self, preorder, inorder):        """        :type preorder: List[int]        :type inorder: List[int]        :rtype: TreeNode        """        if not inorder:            return        i=0        for i in range(len(inorder)):            if inorder[i]==preorder[0]:                break        root=TreeNode(preorder[0])        root.left=self.buildTree(preorder[1:i+1],inorder[:i])        root.right=self.buildTree(preorder[i+1:],inorder[i+1:])        return roots=Solution()s.buildTree([3,9,20,15,7],[9,3,15,20,7])