# Definition for a binary tree node.# class TreeNode:#     def __init__(self, x):#         self.val = x#         self.left = None#         self.right = Noneclass Solution:    def sortedArrayToBST(self, nums: List[int]) -> TreeNode:        def buildBST(nums):            if not nums:                return            mid = int(len(nums)/2)            root = TreeNode(nums[mid])            root.left = buildBST(nums[:mid])            root.right = buildBST(nums[mid+1:])            return root        return buildBST(nums)        