# Definition for a binary tree node.# class TreeNode:#     def __init__(self, x):#         self.val = x#         self.left = None#         self.right = Noneclass Solution:    def minDepth(self, root):        """        :type root: TreeNode        :rtype: int        """        if not root:            return 0        def helper(root):            if not root:                return float("inf")            if root.left == None and root.right == None:                return 1            return min(helper(root.left), helper(root.right)) + 1        return helper(root)s=Solution()root=TreeNode(1)root.left=TreeNode(2)print(s.minDepth(root))