class Solution(object):    def longestConsecutive(self, nums):        """        :type nums: List[int]        :rtype: int        """        res=0        nums = set(nums)        for num in nums:            if num-1 not in nums:                temp=1                while num+1 in nums:                    num+=1                    temp+=1                if temp>res:                    res=temp        return res"""class Solution(object):    def longestConsecutive(self, nums):        :type nums: List[int]        :rtype: int        if len(nums)==0:            return 0        x=max(nums)        index=[0 for i in range(x+1)]        for i in nums:            index[i]=1        res=0        temp=0        for i in range(len(index)):            if index[i]==1:                temp+=1            else:                if temp>res:                    res=temp                temp=0        return res"""solution=Solution()nums = [100, 4, 200, 1, 3, 2]print(solution.longestConsecutive(nums))