class Solution(object):    def candy(self, ratings):        """        :type ratings: List[int]        :rtype: int        """        n=len(ratings)        if n==0:            return 0        if n==1:            return 1        nums=[1 for i in range(n)]        for i in range(1,n-1,1):            if ratings[i]>ratings[i+1]:                nums[i]=max(nums[i],nums[i+1]+1)            if ratings[i]>ratings[i-1]:                nums[i]=max(nums[i],nums[i-1]+1)        for i in range(n-2,0,-1):            if ratings[i]>ratings[i-1]:                nums[i]=max(nums[i],nums[i-1]+1)            if ratings[i]>ratings[i+1]:                nums[i]=max(nums[i],nums[i+1]+1)        if ratings[0]>ratings[1]:            nums[0]=max(nums[0],nums[1]+1)        if ratings[n-1]>ratings[n-2]:            nums[n-1]=max(nums[n-1],nums[n-2]+1)        return sum(nums)