# Definition for a binary tree node.class TreeNode:    def __init__(self, x):        self.val = x        self.left = None        self.right = Noneclass Solution:    def preorderTraversal(self, root):        """        :type root: TreeNode        :rtype: List[int]        """        self.ans=[]        def helper(root):            self.ans.append(root.val)            if root.left:                helper(root.left)            if root.right:                helper(root.right)            return        if not root:            return []        else:            helper(root)        return self.anss=Solution()root=TreeNode(1)root.right=TreeNode(2)root.right.left=TreeNode(3)print(s.preorderTraversal(root))