class Solution:    def findPeakElement(self, nums):        """        :type nums: List[int]        :rtype: int        """        if not nums:            return        n = len(nums)        if n == 1:            return 0        if n == 2:            if nums[1] > nums[0]:                return 1            else:                return 0        if nums[0] > nums[1]:            return 0        for i in range(1, n - 1, 1):            if nums[i] > nums[i - 1] and nums[i] > nums[i + 1]:                return i        if nums[n - 1] > nums[n - 2]:            return n - 1s=Solution()A=[0,2,1,0]print(s.findPeakElement(A))