# Definition for singly-linked list.class ListNode:     def __init__(self, x):         self.val = x         self.next = Noneclass Solution:    def removeElements(self, head, val):        """        :type head: ListNode        :type val: int        :rtype: ListNode        """        cur=ListNode(-1)        cur.next=head        current=cur        while current.next!=None:            if current.next.val==val:                current.next=current.next.next                #return cur.next            else:                current=current.next        return cur.nexts=Solution()head1,head2,head3,head4,head5,head6=ListNode(1),ListNode(2),ListNode(3),ListNode(4),ListNode(5),ListNode(6)head1.next=head2head2.next=head3head3.next=head4head4.next=head5head5.next=head6k=3print(s.removeElements(head1,k))