# Definition for a binary tree node.# class TreeNode:#     def __init__(self, x):#         self.val = x#         self.left = None#         self.right = Noneclass Solution:    def countNodes(self, root: TreeNode) -> int:        self.count = 0        def count(root):            if not root:                return            self.count += 1            count(root.left)            count(root.right)        count(root)        return self.count        