import heapqclass Solution(object):    def maxSlidingWindow(self, nums, k):        """        :type nums: List[int]        :type k: int        :rtype: List[int]        """        n=len(nums)        if n==0:            return []        ans=[]        heap1=[]        for i in range(k-1):            heapq.heappush(heap1,nums[i])        for i in range(n-k+1):            heap1.append(nums[i+k-1])            ans.append(heapq.nlargest(1,heap1)[0])            heap1.remove(nums[i])        return anss=Solution()nums = []k = 0print(s.maxSlidingWindow(nums,k))#从n个数当中找最大 最小值或者删除一个数 都用heapq