class Solution:    def lengthOfLIS(self, nums):        tails = [0] * len(nums)        size = 0        for x in nums:            i, j = 0, size            while i != j:#二分法找比x的lower bound，最小的大于等于x的元素                m = int((i + j) / 2)                if tails[m] < x:                    i = m + 1                else:                    j = m            tails[i] = x            size = max(i + 1, size)        return size