class Solution(object):    def maxNumber(self, nums1, nums2, k):        def maximize(nums,length):            dp,i = {length:nums},0            while (length):                while (i+1<length and nums[i]>=nums[i+1]): i+=1                nums,length = nums[:i]+nums[i+1:],length-1                dp[length] = nums                if i>0: i-=1            return dp        m,n,result = len(nums1),len(nums2),[]        dp1,dp2 = maximize(nums1,m),maximize(nums2,n)        for i in range(min(m+1,k+1)):            if k-i not in dp2: continue            result = max(result,[max(dp1[i],dp2[k-i]).pop(0) for _ in range(k)])        return result