class Solution(object):    def reverseVowels(self, s):        list1=list(s)        n=len(list1)        nums=[]        for i in range(n):            if list1[i] in "aeiouAEIOU":                nums.append(i)        for k in range (len(nums)//2):            list1[nums[k]],list1[nums[-k-1]]=list1[nums[-k-1]],list1[nums[k]]        return "".join(j for j in list1)s = Solution()str="leetcode"print(s.reverseVowels(str))"""如果最后是list1循环而不是nums循环可能会超时"""