class Solution(object):    def firstUniqChar(self, s):        """        :type s: str        :rtype: int        """        dict={}        n=len(s)        for i in range(n):            if s[i] not in dict:                dict[s[i]]=1            else:                dict[s[i]]+=1        for i in range(n):            if dict[s[i]]==1:                return i        return -1s = Solution()str="abcdefg"print(s.firstUniqChar(str))