class Solution:    def combinationSum(self, candidates, target):        """        :type candidates: List[int]        :type target: int        :rtype: List[List[int]]        """        def helper(candi,target,path):            if target<0:                return            elif target==0:                self.ans.append(path)            else:                for i in range(len(candi)):                    helper(candi[i:],target-candi[i],path+[candi[i]])        self.ans=[]        helper(candidates,target,[])        return self.anss=Solution()print(s.combinationSum([2,3,6,7],7))