class Solution:    def combinationSum2(self, candidates, target):        """        :type candidates: List[int]        :type target: int        :rtype: List[List[int]]        """        def helper(candi,target,path):            if target<0:                return            elif target==0:                if path not in self.ans:                    self.ans.append(path)            else:                for i in range(len(candi)):                    helper(candi[i+1:],target-candi[i],path+[candi[i]])        self.ans=[]        candidates.sort()        helper(candidates,target,[])        return self.anss=Solution()print(s.combinationSum([2,3,6,7],7))