class Solution(object):    def findNthDigit(self, n):        """        :type n: int        :rtype: int        """        def nth(n):            count=0            while n>10**count*9*(count+1):                n-=10**count*9*(count+1)                count+=1            return count,n        dig,num=nth(n)        number=int(num/(dig+1))        yu=num%(dig+1)        if yu==0:            return int(str(10**dig+number-1)[-1])        else:            return int(str(10**dig+number)[yu-1])s=Solution()k=100print(s.findNthDigit(k))