class Solution:    def canCross(self, stones):        self.memo = set()        self.target = stones[-1]        self.stones_set = set(stones)        self.res = False        def jump(start, step):            if (start, step) in self.memo:                return            self.memo.add((start, step))            if start == self.target:                self.res = True                return            if start + step - 1 in self.stones_set and step - 1 > 0:                jump(start + step - 1, step - 1)            if start + step in self.stones_set and step > 0:                jump(start + step, step)            if start + step + 1 in self.stones_set:                jump(start + step + 1, step + 1)            return        jump(stones[0], 0)        return self.res