# Definition for a binary tree node.class TreeNode:     def __init__(self, x):         self.val = x         self.left = None         self.right = Noneclass Solution:    def sumOfLeftLeaves(self, root):        """        :type root: TreeNode        :rtype: int        """        self.sum=0        def isnode(root):            if root==None:                return            if root.left==None and root.right==None:                self.sum+=root.val            return        def helper(root):            if root==None:                return            isnode(root.left)            helper(root.left)            helper(root.right)            return        if root==None or (root.left==None and root.right==None):            return 0        helper(root)        return self.sums=Solution()root=TreeNode(3)root.left=TreeNode(7)root.right=TreeNode(9)root.right.left=TreeNode(15)root.right.right=TreeNode(9)print(s.sumOfLeftLeaves(root))