class Solution:    def countBattleships(self, board):        """        :type board: List[List[str]]        :rtype: int        """        def goright(isvisited,board,i,j):            if j>=len(board[0]) or isvisited[i][j]:                return            isvisited[i][j]=1            if board[i][j]=="X":                goright(isvisited,board,i,j+1)            return        def godown(isvisited,board,i,j):            if i>=len(board) or isvisited[i][j]:                return            isvisited[i][j]=1            if board[i][j]=="X":                godown(isvisited,board,i+1,j)            return        if board ==None or board[0]==None:            return 0        ans=0        n,m=len(board),len(board[0])        isvisited=[[0 for i in range(m)]for j in range(n)]        for i in range(n):            for j in range(m):                if isvisited[i][j]==1:                    continue                if isvisited[i][j]==0:                    isvisited[i][j] = 1                    if board[i][j] == "X":                        ans+=1                        goright(isvisited,board,i,j+1)                        godown(isvisited,board,i+1,j)        return anss=Solution()board =[["x",".",".","x"],[".",".",".","x"],[".",".",".","x"]]print(s.countBattleships(board))