class Solution:    def findWords(self, words):        """        :type words: List[str]        :rtype: List[str]        """        nums1="QWERTYUIOPqwertyuiop"        nums2="ASDFGHJKLasdfghjkl"        nums3="ZXCVBNMzxcvbnm"        ans=[]        for i in range(len(words)):            index=1            if words[i][0] in nums1:                for j in range(len(words[i])):                    if words[i][j] not in nums1:                        index=0                        break            elif words[i][0] in nums2:                for j in range(len(words[i])):                    if words[i][j] not in nums2:                        index=0                        break            elif words[i][0] in nums3:                for j in range(len(words[i])):                    if words[i][j] not in nums3:                        index=0                        break            if index:                ans.append(words[i])        return answords=["Hello", "Alaska", "Dad", "Peace"]s=Solution()print(s.findWords(words))