class Solution:    def nextGreaterElements(self, nums):        """        :type nums: List[int]        :rtype: List[int]        """        n=len(nums)        ans = [-1 for i in range(n)]        stack=[]        for i in range(n):            while stack and nums[i]>nums[stack[-1]]:                temp=stack[-1]                stack.pop()                ans[temp]=nums[i]            stack.append(i)        for i in range(n):            while stack and nums[i]>nums[stack[-1]]:                temp=stack[-1]                stack.pop()                ans[temp]=nums[i]            stack.append(i)        return anss=Solution()nums=[1,2,1]print(s.nextGreaterElements(nums))#常规方法肯定会超时 用stack