class Solution(object):    def change(self, amount, coins):        """        :type amount: int        :type coins: List[int]        :rtype: int        """        nums =[0 for i in range(amount+1)]        nums[0]=1        for coin in coins:            if coin>amount:                continue            for j in range(coin,amount+1,1):                nums[j]+=nums[j-coin]        return nums[amount]s=Solution()amount=5coins=[1,2,5]print(s.change(amount,coins))#DP