class Solution(object):    def findCircleNum(self, M):        """        :type M: List[List[int]]        :rtype: int        """        n=len(M)        if n==1:            return 1        friends=[[]for i in range(n)]        for i in range(n):            for j in range(n):                if M[i][j]==1 and i!=j:                    friends[i].append(j)        isvisited=[0 for i in range(n)]        def friend(i,friends,isvisited):            for j in range(len(friends[i])):                if isvisited[friends[i][j]]==0:                    isvisited[friends[i][j]]=1                    friend(friends[i][j],friends,isvisited)            return        count=0        for k in range(n):            if isvisited[k]==0:                isvisited[k]=1                friend(k,friends,isvisited)                count+=1        return counts = Solution()M=[[1,1,0], [1,1,1], [0,1,1]]print(s.findCircleNum(M))