class Solution(object):    def canPlaceFlowers(self, flowerbed, n):        """        :type flowerbed: List[int]        :type n: int        :rtype: bool        """        if n==0:            return True        if len(flowerbed)==1:            if flowerbed[0]==0 and n<=1:                return  True            else:                return False        count=0        if flowerbed[0]==0 and flowerbed[1]==0:            flowerbed[0]=1            count+=1        for i in range(1,len(flowerbed)-1):            if flowerbed[i-1]==0 and flowerbed[i]==0 and flowerbed[i+1]==0:                flowerbed[i]=1                count+=1        if flowerbed[len(flowerbed)-1] == 0 and flowerbed[len(flowerbed)-2] == 0:            count+=1        if count>=n:            return True        else:            return Falses = Solution()nums = [1,0,0,0,1,0,0]n = 2print(s.canPlaceFlowers(nums,n))