import mathclass Solution(object):    def judgeSquareSum(self, c):        """        :type c: int        :rtype: bool        """        i,j=0,int(math.sqrt(c))        while i<=j:            if c==i*i+j*j:                return True            elif c<i*i+j*j:                j-=1            else:                i+=1        return Falses=Solution()k=5print(s.judgeSquareSum(k))