class Solution(object):    def countSubstrings(self, s):        """        :type s: str        :rtype: int        """        n=len(s)        num=0        for center in range(n):            length=0            while center-length>=0 and center+length<n:                if s[center-length]==s[center+length]:                    num+=1                else:                    break                length+=1            if center<n-1 and s[center]==s[center+1]:                length2=0                while center-length2>=0 and center+1+length2<n:                    if s[center-length2]==s[center+1+length2]:                        num+=1                    else:                        break                    length2+=1        return nums1="leetcode"s=Solution()print(s.countSubstrings(s1))#分两种情况，奇数偶数