class TreeNode:    def __init__(self, x):        self.val = x        self.left = None        self.right = Noneclass Solution:    def constructMaximumBinaryTree(self, nums):        """        :type nums: List[int]        :rtype: TreeNode        """        if nums==[]:            return None        index=0        for i in range(1,len(nums),1):            if nums[i]>nums[index]:                index=i        root=TreeNode(nums[index])        root.left=self.constructMaximumBinaryTree(nums[:index])        root.right=self.constructMaximumBinaryTree(nums[index+1:])        return roots=Solution()nums=[3,2,1,6,0,5]print(s.constructMaximumBinaryTree(nums))