from collections import defaultdictclass Solution:    def findClosestElements(self, arr, k, x):        """        :type arr: List[int]        :type k: int        :type x: int        :rtype: List[int]        """        ans=[]        dict=defaultdict(list)        for i in range(len(arr)):            dict[abs(arr[i]-x)].append(arr[i])        for item in sorted(dict.items()):            if k>=len(item[1]):                ans+=item[1]                k-=len(item[1])            else:                for i in range(k):                    ans.append(item[1][i])                break        ans.sort()        return anss=Solution()arr = [1,2,3,4,5]x = 3k=4print(s.findClosestElements(arr,k,x))