class Solution(object):    def findLengthOfLCIS(self, nums):        """        :type nums: List[int]        :rtype: int        """        if len(nums)==0:            return 0        index,ans=1,1        for i in range(1,len(nums)):            if nums[i]>nums[i-1]:                index+=1            else:                ans=max(index,ans)                index=1        ans = max(index, ans)        return anss = Solution()nums = [1,3,5,7]print(s.findLengthOfLCIS(nums))