class Solution(object):    def validPalindrome(self, s):        """        :type s: str        :rtype: bool        """        def ispal(s):            n=len(s)            for i in range(n):                if s[i]==s[n-1-i]:                    continue                else:                    return False            return True        if len(s)<=1:            return True        left,right=0,len(s)-1        while s[left]==s[right]:            left+=1            right-=1            if left>=right:                return True        s1=s[:left]+s[left+1:]        s2=s[:right]+s[right+1:]        if ispal(s1) or ispal(s2):            return True        else:            return Falses = Solution()str="aba"print(s.validPalindrome(str))# 找到不同的两个字母，删除之后对两个字符串分别判断是否是pal