class Solution:    def mySqrt(self, x):        if x==0 or x==1:            return x        if x==2:            return 1        left,right=0,x-1        while True:            mid=int((left+right)/2)            if mid*mid>x:                right=mid-1            elif mid*mid==x:                return mid            else:                if (mid+1)*(mid+1)>x:                    return mid                else:                    left=mid+1s=Solution()x=10print(s.mySqrt(x))