class Solution:    def hasAlternatingBits(self, N):        """        :type n: int        :rtype: bool        """        nums=[]        while N:            yu=N%2            nums.append(yu)            N=int((N-yu)/2)        if len(nums)<2:            return True        for i in range(0,len(nums),2):            if nums[0]!=nums[i]:                return False        for j in range(1,len(nums),2):            if nums[1]!=nums[j]:                return False        if nums[0]!=nums[1]:            return True        else:            return Falses=Solution()N=11print(s.hasAlternatingBits(N))