class MyHashSet:    def __init__(self):        """        Initialize your data structure here.        """        number = 1000001        self.nums = [0 for i in range(number)]    def add(self, key):        """        :type key: int        :rtype: void        """        self.nums[key] = 1    def remove(self, key):        """        :type key: int        :rtype: void        """        self.nums[key] = 0    def contains(self, key):        """        Returns true if this set contains the specified element        :type key: int        :rtype: bool        """        if self.nums[key] == 1:            return True        else:            return False# Your MyHashSet object will be instantiated and called as such:# obj = MyHashSet()# obj.add(key)# obj.remove(key)# param_3 = obj.contains(key)nums=[0,1,0]k=0s=Solution()print(s.checkSubarraySum(nums,k))