class Solution(object):    def pivotIndex(self, nums):        """        :type nums: List[int]        :rtype: int        """        if len(nums)==0:            return -1        left,right=[0 for i in range(len(nums))],[0 for i in range(len(nums))]        left[0]=nums[0]        for i in range(1,len(nums)):            left[i]=nums[i]+left[i-1]        right[len(nums)-1]=nums[len(nums)-1]        for j in range(len(nums)-2,-1,-1):            right[j]=right[j+1]+nums[j]        for k in range(len(nums)):            if left[k]==right[k]:                return k        return -1s = Solution()nums = []print(s.pivotIndex(nums))