class Solution:    def networkDelayTime(self, times, N, K):        nodes, distance = set(), [float("inf") for i in range(N+1)]        distance[0] = 0        for i in range(1, N+1):            if i == K:                distance[K] = 0            else:                nodes.add(i)        while nodes:            temp_distance = float('inf')            temp = -1            for start, end, w in times:                if end in nodes:                    if distance[start] + w < temp_distance:                        temp_distance = distance[start] + w                        temp = end            if temp_distance == float("inf"): return -1            distance[temp] = temp_distance            nodes.remove(temp)        return max(distance)