class Solution:    def removeDuplicates(self, nums):        """        :type nums: List[int]        :rtype: int        """        isdup,i=0,1        while i<len(nums):            if nums[i]==nums[i-1]:                if isdup==0:                    isdup=1                    i+=1                    continue                else:                    nums.pop(i)                    i-=1            else:                isdup=0            i+=1        return len(nums)s=Solution()nums=[1,1,1,2,2,3]print(s.removeDuplicates(nums))