class Solution:    def largestRectangleArea(self, heights):        ans = 0        n=len(heights)        heights.append(0)        stack = [-1]        for i in range(n + 1):            while heights[i] < heights[stack[-1]]:                h = heights[stack.pop()]                w = i - 1 - stack[-1]                ans = max(ans, h * w)            stack.append(i)        return anss=Solution()nums=[2,1,5,6,2]print(s.largestRectangleArea(nums))#这是递增栈#从栈中退出来的都是比current更大的，表示可以一直向前推