class Solution:    def find_mountain(self, A, start):        # return is a mountian, and end, next_start        n = len(A)        if start > n-3: return False, -1, n        if A[start] >= A[start+1]: return False, -1, start+1        i = 0        for i in range(start+1, n-1):            if A[i] == A[i+1]: return False, -1, i+1            elif A[i+1]<A[i]:                break        if i==n-2 and A[i+1] >= A[i]: return False, -1, n        for index in range(i+1, n, 1):            if A[index]>=A[index-1]:                return True, index-1, index-1        return True, n-1, n    def longestMountain(self, A):        res, start, n = 0, 0, len(A)        while start<n:            flag, end, next_start = self.find_mountain(A, start)            if flag:                res = max(res, end - start + 1)            start = next_start        return res