class Solution:    def binaryGap(self, N):        """        :type N: int        :rtype: int        """        nums=[]        while N:            yu=N%2            nums.append(yu)            N=int((N-yu)/2)        index=0        for i in range(len(nums)):            if nums[i]==1:                pre=i                for j in range(i+1,len(nums),1):                    if nums[j]==1:                        index=max(index,j-pre)                        pre=j                break        return indexs=Solution()N=6print(s.binaryGap(N))