class Solution:    def findAndReplacePattern(self, words, pattern):        """        :type words: List[str]        :type pattern: str        :rtype: List[str]        """        n=len(pattern)        ans=[]        for item in words:            flag=1            dict1,dict2={},{}            for i in range(n):                if item[i] not in dict1:                    dict1[item[i]]=pattern[i]                if pattern[i] not in dict2:                    dict2[pattern[i]]=item[i]                if dict1[item[i]]!=pattern[i] or dict2[pattern[i]]!=item[i]:                    flag=0                    break            if flag==1:                ans.append(item)        return anss=Solution()words = ["abc","deq","mee","aqq","dkd","ccc"]pattern = "abb"print(s.findAndReplacePattern(words,pattern))