class Solution:    def sortArrayByParity(self, A):        """        :type A: List[int]        :rtype: List[int]        """        n=len(A)        left,right=0,n-1        while True:            while left<n:                if A[left]%2==1:                    break                left+=1            while right>=0:                if A[right]%2==0:                    break                right-=1            if left<right:                A[left],A[right]=A[right],A[left]            else:                break        return Aobj = Solution()print(obj.sortArrayByParity([1,3,2,4]))