# Definition for singly-linked list.# class ListNode(object):#     def __init__(self, x):#         self.val = x#         self.next = Noneclass Solution(object):    def removeNthFromEnd(self, head, n):        """        :type head: ListNode        :type n: int        :rtype: ListNode        """        root=copy=ListNode(0)        root.next=head        lists=[]        while root:            lists.append(root)            root=root.next        number=len(lists)        if n==1:            lists[number-n-1].next=None        else:            lists[number-n-1].next=lists[number-n+1]        return copy.next